    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_details);

        this.indexSelected = getIntent().getIntExtra("index", 0);
        System.out.println(this.indexSelected + "   hhhhh");
        Typeface tf = Typeface.createFromAsset(getAssets(), "adobe-arabic-bold-1.ttf");
        this.textnames = (TextView) findViewById(R.id.names);
        this.textnames.setTypeface(tf);

        this.textdesc = (TextView) findViewById(R.id.namesdesc);
        this.textdesc.setTypeface(tf);

        this.title = (TextView) findViewById(R.id.title);
        readTxt(getResources().openRawResource(R.raw.allah_names));

        this.b1 = (ImageButton) findViewById(R.id.next);
        this.b2 = (ImageButton) findViewById(R.id.previous);

        this.textnames.setText(ArabicUtilities.reshape(this.name[this.indexSelected]));
        this.textdesc.setText("\n" + ArabicUtilities.reshape((String) this.english.get(this.indexSelected)));
        this.title.setText(ArabicUtilities.reshape(this.namedesc[this.indexSelected]));


        //For Next Button
        this.b1.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                MainActivity.ads.showInterstitial(false);
                Details details = Details.this;
                details.indexSelected++;
                if (Details.this.indexSelected > 185) {
                    Details.this.indexSelected = 0;
                }
                //Details.this.slidingimage.setImageResource(Details.this.imgid[Details.this.indexSelected].intValue());
                Details.this.textnames.setText(ArabicUtilities.reshape(Details.this.name[Details.this.indexSelected]));
                Details.this.textdesc.setText("\n" + ArabicUtilities.reshape((String) Details.this.english.get(Details.this.indexSelected)));
                Details.this.title.setText(ArabicUtilities.reshape(Details.this.namedesc[Details.this.indexSelected]));
            }
        });


        //For Previous Button
        this.b2.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                MainActivity.ads.showInterstitial(false);
                Details details = Details.this;
                details.indexSelected--;
                if (Details.this.indexSelected < 0) {
                    Details.this.indexSelected = 185;
                }
                //Details.this.slidingimage.setImageResource(Details.this.imgid[Details.this.indexSelected].intValue());
                Details.this.textnames.setText(ArabicUtilities.reshape(Details.this.name[Details.this.indexSelected]));
                Details.this.textdesc.setText("\n" + ArabicUtilities.reshape((String) Details.this.english.get(Details.this.indexSelected)));
                Details.this.title.setText(ArabicUtilities.reshape(Details.this.namedesc[Details.this.indexSelected]));
            }
        });
    }

    private void readTxt(InputStream inputStream) {
        ReadFile(inputStream);
    }

    String ReadFile(InputStream paramInputStream) {
        String localObject = "";
        try {
            BufferedReader localBufferedReader = new BufferedReader(new InputStreamReader(paramInputStream, "UTF-8"));
            while (true) {
                String str1 = localBufferedReader.readLine();
                if (str1 == null) {
                    break;
                }
                String str2 = new StringBuilder(String.valueOf(localObject)).append(str1.trim()).append("\n").toString();
                this.english.add(str1.trim());
                localObject = str2;
            }
            Log.v("Stream Start", "Start of Stream");
            Log.v("Data", localObject);
            Log.v("Stream End", "End of Stream");
        } catch (Exception localException) {
            localException.printStackTrace();
        }
        return localObject;
    }
